import React, { useEffect, useState } from 'react';
import requestAPI from '../services/requestAPI';
import StarWarsContext from './StarwarsContext';

function Provider({ children }) {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState('');
  const [searchValue, setSearchValue] = useState('');
  const [selectedOpt, setSelectedOpt] = useState('');

  const handleChange = (e) => {
    setSearchValue(e.target.value);
  };

  useEffect(() => {
    requestAPI().then((result) => setData(result));
  }, []);

  useEffect(() => {
    const searchFilter = data.filter((ele) => (
      ele.name.toUpperCase().includes(searchValue.toUpperCase())
    ));
    setFilter(searchFilter);
  }, [searchValue, data]);

  useEffect(() => {
    const { column, compare, numbers } = selectedOpt;

    let filtered = [];
    switch (compare) {
    case 'maior que':
      filtered = data.filter((e) => +e[column] > +numbers);
      break;
    case 'menor que':
      filtered = data.filter((e) => +e[column] < +numbers);
      break;
    case 'igual a':
      filtered = data.filter((e) => +e[column] === +numbers);
      break;
    default:
      filtered = data;
    }
    setFilter(filtered);
  }, [selectedOpt, data]);

  const val = {
    data,
    handleChange,
    searchValue,
    filter,
    selectedOpt,
    setSelectedOpt,
  };

  return <StarWarsContext.Provider value={ val }>{ children }</StarWarsContext.Provider>;
}

Provider.propTypes = {}.isRequired;

export default Provider;
