import React, { useEffect, useState } from 'react';
import requestAPI from '../Api/requestApi';
import StarWarsContext from './StarwarsContext';

function Provider({ children }) {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState('');
  const [searchValue, setSearchValue] = useState('');


  const handleChange = (e) => {
    setSearchValue(e.target.value);
  };

  useEffect(() => {
    requestAPI().then((result) => setData(result));
  }, []);

  useEffect(() => {
    const searchFilter = data.filter((ele) => (
      ele.name.toUpperCase().includes(searchValue.toUpperCase())
    ));
    setFilter(searchFilter);
  }, [searchValue, data]);


  const val = { data, filter, searchValue, handleChange };

  return <StarWarsContext.Provider value={ val }>{ children }</StarWarsContext.Provider>;
}

Provider.propTypes = {}.isRequired;

export default Provider;
