import React, { Component } from 'react';
import md5 from 'crypto-js/md5';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends Component {
  render() {
    const { gravatarEmail, nome, score } = this.props;
    const email = md5(gravatarEmail).toString();
    return (
      <>
        <img data-testid="header-profile-picture" alt="profile-img" src={ `https://www.gravatar.com/avatar/${email}` } />
        <p data-testid="header-player-name">{ nome }</p>
        <p data-testid="header-score">{ score }</p>
        <div>Header</div>
      </>
    );
  }
}

Header.propTypes = {
  gravatarEmail: PropTypes.string.isRequired,
  nome: PropTypes.string.isRequired,
  score: PropTypes.string.isRequired,
};

const mapStateToProps = (globalState) => ({
  gravatarEmail: globalState.user.gravatarEmail,
  nome: globalState.user.name,
  score: globalState.user.score,
});

export default connect(mapStateToProps)(Header);
