import { USER_LOGIN } from '../actions';

const INITIAL_STATE = {
  name: '',
  assertions: '',
  gravatarEmail: '',
  score: '',
};

const playerReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case USER_EMAIL:
    return {
      ...state,
      gravatarEmail: action.gravatarEmail,
    };
  default:
    return state;
  }
};
export default playerReducer;
