              {' '}
              <code>audio</code>
              .
            </audio>
            <label htmlFor="favorite">
              Favorita
              <input
                data-testid={ `checkbox-music-${album.trackId}` }
                id="favorite"
                type="checkbox"
                onClick={ this.addFavorites }
                defaultChecked={ check }
              />
            </label>
          </div>)
    );
  }
}

MusicCard.propTypes = {
  album: PropTypes.shape().isRequired,
};

export default MusicCard;
