import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../component/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Load from './Load';

export default class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      search: '',
      buttonDisabled: true,
      listAlbum: [],
      cleanValue: '',
      loading: false,
      firstClick: true,
    };
  }

  mudança = (event) => {
    const { value } = event.target;
    this.setState({ search: value }, this.disabledButton);
    // console.log(this.state);
  };

  disabledButton = () => {
    const { search } = this.state;
    if (search.length >= 2) {
      this.setState({ buttonDisabled: false });
    } else {
      this.setState({ buttonDisabled: true });
    }
    console.log(this.state);
  };

  searchClean = async () => {
    const { search } = this.state;
    this.setState({ loading: true, cleanValue: search, firstClick: false });
    const objetoArtista = await searchAlbumsAPI(search);
    this.setState({
      listAlbum: objetoArtista, loading: false, search: '',
    }, this.disabledButton);
    // console.log(this.state);
  };

  render() {
    const { buttonDisabled, listAlbum,
      loading, search, firstClick, cleanValue } = this.state;

    const validando = (
      firstClick ? (<> </>) : <p>Nenhum álbum foi encontrado</p>
    );
    const divAlbuns = (
      <>
        <p>
          Resultado de álbuns de:
          {' '}
          {cleanValue}
        </p>
        <section>
          {
            listAlbum.length === 0 ? <p>Nenhum álbum foi encontrado</p>
              : listAlbum.map((element) => (
                <div
                  key={ `${element.collectionName}_${element.collectionId}` }
                >
                  <div>
                    <p>{element.collectionName}</p>
                    <Link
                      to={ `/album/${element.collectionId}` }
                      data-testid={ `link-to-album-${element.collectionId}` }
                    >
                      <p>
                        <img
                          src={ element.artworkUrl100 }
                          alt={ element.collectionName }
                        />
                      </p>
                    </Link>
                    <p>
                      {element.artistName}
                    </p>
                  </div>
                </div>
              ))
          }

        </section>
      </>
    );

    return (
      <div
        data-testid="page-search"
      >
        <Header />
        {loading ? <Load /> : (
          <form>
            <label htmlFor="Pesquisa">
              <input
                type="text"
                value={ search }
                data-testid="search-artist-input"
                placeholder="Nome do Artista"
                onChange={ this.mudança }
              />
            </label>
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={ buttonDisabled }
              onClick={ this.searchClean }
            >
              Procurar
            </button>
          </form>
        )}
        { (listAlbum.length > 0)
          ? divAlbuns
          : validando}
        <div />
      </div>
    );
  }
}
