import React from 'react';
import { NavLink } from 'react-router-dom';
import { getUser } from '../services/userAPI';
import Loading from './Loading';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '',
      loading: true,
    };
  }

  async componentDidMount() {
    const username = await getUser();
    this.setState({ loading: false, name: username });
  }

  render() {
    const { name, loading } = this.state;
    return (
      <header data-testid="header-component">
        { loading ? <Loading />
          : (
            <>
              <div data-testid="header-user-name">{name.name}</div>
              <NavLink to="/search" data-testid="link-to-search" />
              <NavLink to="/favorites" data-testid="link-to-favorites" />
              <NavLink to="/profile" data-testid="link-to-profile" />
            </>
          ) }
      </header>
    );
  }
}

export default Header;
