import React from 'react';
import Header from '../component/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      searchBar: '',
      btnDisabled: true,
      clearSearchBar: '',
      loading: false,
      listAlbum: [],
      firstClick: true,
    };
  }

  disparaEvent = (event) => {
    const { value } = event.target;
    this.setState({
      searchBar: value }, this.buttonDisabled);
  };

  buttonDisabled = () => {
    const { searchBar } = this.state;
    if (searchBar.length > 1) {
      this.setState({ btnDisabled: false });
    } else {
      this.setState({ btnDisabled: true });
    }
  };

  clearSearchBar = async () => {
    const { searchBar } = this.state;
    this.setState({ loading: true, clearSearchBar: searchBar, firstClick: false });
    const objctArt = await searchAlbumsAPI(searchBar);
    this.setState({
      listAlbum: objctArt,
      loading: false,
      searchBar: '' }, this.buttonDisabled);
  };

  render() {
    const {
      btnDisabled,
      searchBar,
      clearSearchBar,
      loading,
      listAlbum,
      firstClick } = this.state;

    const check = (
      firstClick ? (<> </>) : <h3>Nenhum álbum foi encontrado</h3>
    );

    const textResult = (
      <section>

        <p>
          Resultado de álbuns de :
          { searchBar }
          { clearSearchBar }
        </p>
      </section>
    );

    return (
      <div data-testid="page-search">
        <Header />
        <label htmlFor="searchBar">
          <input
            type="text"
            data-testid="search-artist-input"
            name="searchBar"
            placeholder="Artista ou Música"
            onChange={ this.disparaEvent }
          />
        </label>
        <button
          type="button"
          data-testid="search-artist-button"
          disabled={ btnDisabled }
          // onClick={}
        >
          Procurar
        </button>
      </div>
    );
  }
}

export default Search;
