import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../component/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Load from './Load';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      searchBar: '',
      btnDisabled: true,
      clearSearchBar: '',
      loading: false,
      listAlbum: [],
      firstClick: true,
    };
  }

  disparaEvent = (event) => {
    const { value } = event.target;
    this.setState({
      searchBar: value }, this.buttonDisabled);
  };

  buttonDisabled = () => {
    const { searchBar } = this.state;
    if (searchBar.length >= 2) {
      this.setState({ btnDisabled: false });
    } else {
      this.setState({ btnDisabled: true });
    }
    console.log(this.state);
  };

  clearSearchBar = async () => {
    const { searchBar } = this.state;
    this.setState({ loading: true, clearSearchBar: searchBar, firstClick: false });
    const objct = await searchAlbumsAPI(searchBar);
    this.setState({
      listAlbum: objct,
      loading: false,
      searchBar: '' }, this.buttonDisabled);
  };

  render() {
    const {
      btnDisabled,
      searchBar,
      clearSearchBar,
      loading,
      listAlbum,
      firstClick } = this.state;

    const check = (
      firstClick ? (<> </>) : <h3>Nenhum álbum foi encontrado</h3>
    );

    const textResult = (
      <section>
        <p>
          Resultado de álbuns de :
          { searchBar }
          { clearSearchBar }
        </p>
        {
          listAlbum.length === 0 ? <h3>Nenhum álbum foi encontrado</h3>
            : listAlbum.map((ele) => (
              <div
                key={ `${ele.collectionName}-${ele.collectionId}` }
              >
                <div>
                  <p>{ele.collectionName}</p>
                  <Link
                    to={ `/album/${ele.collectionId}` }
                    data-testid={ `link-to-album-${ele.collectionId}` }
                  >
                    <p>
                      <img
                        src={ ele.artworkUrl100 }
                        alt={ ele.collectionName }
                      />
                    </p>
                  </Link>
                  <p>
                    {ele.artistName}
                  </p>
                </div>
              </div>
            ))
        }

      </section>
    );

    return (
      <div data-testid="page-search">
        <Header />
        {loading ? <Load /> : (
          <form>
            <label htmlFor="searchBar">
              <input
                type="text"
                value={ searchBar }
                data-testid="search-artist-input"
                placeholder="Nome do Artista"
                onChange={ this.disparaEvent }
              />
            </label>
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={ btnDisabled }
              onClick={ this.clearSearchBar }
            >
              Procurar
            </button>
          </form>
        )}
        { (listAlbum.length > 0)
          ? textResult
          : check}
        <div />
      </div>
    );
  }
}

export default Search;
