import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { coins, quotationAndInfo } from '../redux/actions';

class WalletForm extends Component {
  state = {
    id: 0,
    value: '0',
    description: '',
    moeda: 'USD',
    paymentMethod: 'Dinheiro',
    tag: 'Alimentação',
    exchangeRates: {},
  };

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(coins());
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  requestApi = async () => {
    const response = await fetch('https://economia.awesomeapi.com.br/json/all');

    const data = await response.json();

    delete data.USDT;

    return data;
  };

  handleClick = async () => {
    const { dispatch } = this.props;
    const exchangeRates = await this.requestApi();

    const item = { ...this.state, exchangeRates };

    dispatch(quotationAndInfo(item));

    this.setState((prevState) => ({
      id: prevState.id + 1,
      value: '',
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates,
    }));
  };

  render() {
    const { currencies } = this.props;
    const { value, description, moeda, paymentMethod, tag } = this.state;
    return (
      <fieldset>
        <label
          htmlFor="valueInput"
        >
          Valor da despesa
          <input
            type="number"
            data-testid="value-input"
            id="valueInput"
            name="value"
            value={ value }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="descInput"
        >
          Descrição da despesa
          <input
            type="text"
            data-testid="description-input"
            id="descInput"
            name="description"
            value={ description }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="currency"
        >
          Moeda
          <select
            data-testid="currency-input"
            value={ moeda }
            name="moeda"
            onChange={ this.handleChange }
            id="currency"
          >
            {currenc.map((e) => <option key={ e } value={ e }>{e}</option>)}
          </select>
        </label>
        <label
          htmlFor="payment"
        >
          Método de pagamento
          <select
            data-testid="method-input"
            id="payment"
            name="payment"
            value={ paymentMethod }
            onChange={ this.handleChange }
          >
            <option value="dinheiro">Dinheiro</option>
            <option value="credito">Cartão de crédito</option>
            <option value="debito">Cartão de débito</option>
          </select>
        </label>
        <label
          htmlFor="tag"
        >
          Tag
          <select
            data-testid="tag-input"
            id="tag"
            name="tag"
            value={ tag }
            onChange={ this.handleChange }
          >
            <option value="alimentacao">Alimentação</option>
            <option value="lazer">Lazer</option>
            <option value="trabalho">Trabalho</option>
            <option value="transporte">Transporte</option>
            <option value="saude">Saúde</option>
          </select>
          <button
            text="Adicionar despesa"
            type="button"
            onClick={ this.handleClick }
          >
            Adicionar despesa
          </button>
        </label>
      </fieldset>
    );
  }
}

const mapStateToProps = (state) => ({
  currency: state.wallet.currencies,
});

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  currency: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export default connect(mapStateToProps)(WalletForm);
