import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class Header extends Component {
  totalExpenses = () => {
    const { expenses } = this.props;
    const total = expenses.length === 0 ? 0 : expenses.reduce((acc, curr) => {
      const coin = curr.currency;
      acc += +curr.value * curr.exchangeRates[coin].ask;
      return acc;
    }, 0);
  };

  render() {
    const { email } = this.props;
    return (
      <div>
        <p>
          <span data-testid="email-field">
            { email }
          </span>
        </p>
        <p>
          Despesa total:
          <span data-testid="total-field">{ this.totalExpenses() }</span>
        </p>
        <p data-testid="header-currency-field">
          BRL
        </p>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  expenses: state.wallet.expenses,
});

Header.propTypes = {
  email: PropTypes.string.isRequired,
  expenses: PropTypes.arrayOf(PropTypes.string.isRequired).isRequired,
};

export default connect(mapStateToProps)(Header);
