export const USER_EMAIL = 'USER_EMAIL';
export const ADD_TOTALSUM = 'ADD_TOTALSUM';
export const ADD_ECONOMIA = 'ADD_ECONOMIA';

export const emailAction = (receivedInfo) => ({
  type: USER_EMAIL,
  email: receivedInfo.email,
});

export const REQUEST = 'REQUEST';
export const SUCCESS = 'SUCCESS';
export const ERROR = 'ERROR';

export const request = () => (
  { type: 'REQUEST' }
);

export const success = (info) => (
  { type: 'SUCCESS',
    info }
);

export const fail = (error) => (
  { type: 'ERROR',
    error }
);

export const coins = () => async (dispatch) => {
  try {
    dispatch(request());
    const response = await fetch('https://economia.awesomeapi.com.br/json/all');
    const data = await response.json();
    delete data.USDT;
    dispatch(success(data));
  } catch (error) {
    dispatch(fail(error));
  }
};

export const addTotalSum = (totalSum, coin) => ({
  type: ADD_TOTALSUM,
  totalSum,
  coin,
});

export const getEconomia = (expenses, exchangeRates) => ({
  type: ADD_ECONOMIA,
  expenses,
  exchangeRates,
});

function requestEconomia() {
  return { type: 'REQUEST_ECONOMIA' };
}

export function fetchEconomia(expenses) {
  return (dispatch) => {
    dispatch(requestEconomia());
    return fetch('https://economia.awesomeapi.com.br/json/all')
      .then((response) => response.json())
      .then((json) => {
        dispatch(getEconomia(expenses, json));
        dispatch(addTotalSum(expenses.value, expenses.currency));
      });
  };
}
