import React, { Component } from 'react';
import { connect } from 'react-redux';

class Header extends Component {
  calculatePrice = () => {
    const { expenses } = this.props;
    const { exchangeRates } = this.state;
    const totalValor = expenses.reduce((acc, curr) => {
      const currencyValues = curr.exchangeRates;
      const actualCurrency = curr.currency;
      const actualValue = curr.value;
      acc += currencyValues[actualCurrency].ask * actualValue;
      return acc;
    }, 0);
    return totalValor.toFixed(2);
  };

  render() {
    const { email } = this.props;
    return (
      <div>
        <span data-testid="email-field">
          {email}
        </span>
        <div>
          <span data-testid="total-field">
            {this.calculatePrice()}
          </span>
          <span data-testid="header-currency-field">
            BRL
          </span>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  email: state.user.email,
  expenses: state.wallet.expenses,
});

Header.propTypes = {}.isRequired;
export default connect(mapStateToProps)(Header);
