import propTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { coins, submitCurrencies } from '../redux/actions';

class WalletForm extends Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: 0,
      description: '',
      moeda: 'USD',
      paymentMethod: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: {},
    };
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  requestApi = async () => {
    const search = await fetch('https://economia.awesomeapi.com.br/json/all');
    const response = await search.json();
    delete response.USDT;
    return response;
  };

  handleClick = () => {
    this.setState((prevState) => ({
      id: prevState.id + 1,
    }));
  };

  render() {
    const { currencies } = this.props;
    const { value, description, moeda, paymentMethod, tag } = this.state;
    return (
      <fieldset>
        <label
          htmlFor="valueInput"
        >
          Valor da despesa
          <input
            type="number"
            data-testid="value-input"
            id="valueInputID"
            name="value"
            value={ value }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="descInput"
        >
          Descrição da despesa
          <input
            type="text"
            data-testid="description-input"
            id="descInputID"
            name="description"
            value={ description }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="currency"
        >
          Moeda
          <select
            data-testid="currency-input"
            type="select"
            value={ moeda }
            name="moeda"
            onChange={ this.handleChange }
            id="currencyID"
          >
            {currencies.map((curr, index) => (
              <option key={ index }>
                {curr}
              </option>
            ))}
          </select>
        </label>
        <label
          htmlFor="payment"
        >
          Método de pagamento
          <select
            data-testid="method-input"
            type="select"
            id="paymentID"
            name="payment"
            value={ paymentMethod }
            onChange={ this.handleChange }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Cartão de credito">Cartão de crédito</option>
            <option value="Cartão de debito">Cartão de débito</option>
          </select>
        </label>
        <label
          htmlFor="tag"
        >
          Tag
          <select
            data-testid="tag-input"
            id="tagID"
            type="select"
            name="tag"
            value={ tag }
            onChange={ this.handleChange }
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Lazer">Lazer</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Transporte">Transporte</option>
            <option value="Saúde">Saúde</option>
          </select>
          <button
            type="button"
            onClick={ this.handleClick }
            data-testid="submit-button"
          >
            Adicionar despesa
          </button>
        </label>
      </fieldset>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  expenses: state.wallet.expenses,
});

WalletForm.propTypes = {
  currencies: propTypes.arrayOf(propTypes.string.isRequired).isRequired,
  dispatch: propTypes.func.isRequired,
};
export default connect(mapStateToProps)(WalletForm);
