import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getCurrencies, getCurrencieValue } from '../helpers/api';

class WalletForm extends Component {
  constructor() {
    super();

    this.state = {
      id: 0,
      value: '0',
      description: '',
      moeda: 'USD',
      paymentMethod: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: {},
    };
  }

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value });
  };

  handleClick = async () => {
    const { expenses, dispatch } = this.props;
    const currenciesValues = await getCurrencieValue();
    this.setState({ exchangeRates: currenciesValues }, () => {
      dispatch(expenseAction(this.state));
      this.setState({ value: '', description: '' });
    });
    this.setState({ id: expenses.length });
  };

  render() {
    const { currencies } = this.props;
    const { value, description, moeda, paymentMethod, tag } = this.state;
    return (
      <fieldset>
        <label
          htmlFor="valueInput"
        >
          Valor da despesa
          <input
            type="number"
            data-testid="value-input"
            id="valueInput"
            name="value"
            value={ value }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="descInput"
        >
          Descrição da despesa
          <input
            type="text"
            data-testid="description-input"
            id="descInput"
            name="description"
            value={ description }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="currency"
        >
          Moeda
          <select
            data-testid="currency-input"
            value={ moeda }
            name="moeda"
            onChange={ this.handleChange }
            id="currency"
          >
            {currencies.map((e) => <option key={ e } value={ e }>{e}</option>)}
          </select>
        </label>
        <label
          htmlFor="payment"
        >
          Método de pagamento
          <select
            data-testid="method-input"
            id="payment"
            name="payment"
            value={ paymentMethod }
            onChange={ this.handleChange }
          >
            <option value="dinheiro">Dinheiro</option>
            <option value="credito">Cartão de crédito</option>
            <option value="debito">Cartão de débito</option>
          </select>
        </label>
        <label
          htmlFor="tag"
        >
          Tag
          <select
            data-testid="tag-input"
            id="tag"
            name="tag"
            value={ tag }
            onChange={ this.handleChange }
          >
            <option value="alimentacao">Alimentação</option>
            <option value="lazer">Lazer</option>
            <option value="trabalho">Trabalho</option>
            <option value="transporte">Transporte</option>
            <option value="saude">Saúde</option>
          </select>
          <button
            type="button"
            onClick={ this.handleClick }
          >
            Adicionar despesa
          </button>
        </label>
      </fieldset>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
});

WalletForm.propTypes = {
  currencies: PropTypes.arrayOf(PropTypes.string).isRequired,
  dispatch: PropTypes.func.isRequired,
};

export default connect(mapStateToProps)(WalletForm);
