import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { emailAction } from '../redux/actions';

class Login extends React.Component {
  state = {
    email: '',
    password: '',
    disabledBtn: true,
  };

  onInputChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, () => this.validateButton());
  };

  validateButton = () => {
    const { email, password } = this.state;
    const validateEmail = /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/;
    const validatePassword = /^.{6,}$/;
    if (validateEmail.test(email) && validatePassword.test(password)) {
      this.setState({ disabledBtn: false });
    } else {
      this.setState({
        disabledBtn: true,
      });
    }
  };

  onClickChange = () => {
    const { email } = this.state;
    const { history, dispatch } = this.props;
    dispatch(emailAction(email));
    history.push('/carteira');
  };

  render() {
    const { email, password, disabledBtn } = this.state;
    return (
      <div>
        <h1>Login</h1>
        <fieldset>
          <label htmlFor="emailInput">
            <input
              data-testid="email-input"
              type="email"
              id="emailInput"
              placeholder="email"
              name="email"
              value={ email }
              onChange={ this.onInputChange }
              required
            />
          </label>

          <label htmlFor="passInput">
            <input
              data-testid="password-input"
              type="password"
              id="passInput"
              name="password"
              placeholder="password"
              value={ password }
              onChange={ this.onInputChange }
              required
            />
          </label>

          <button
            type="submit"
            disabled={ disabledBtn }
            onClick={ this.onClickChange }
          >
            Entrar
          </button>
        </fieldset>
      </div>
    );
  }
}

Login.propTypes = {
  dispatch: PropTypes.func.isRequired,
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default connect()(Login);
