import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { SumTotalExpenses } from '../redux/actions';

class Header extends Component {
  componentDidUpdate() {
    this.sumExpenses();
  }

  sumExpenses = () => {
    const { expenses, dispatch } = this.props;
    let SumTotal = 0;
    expenses.forEach((expense) => {
      SumTotal += (
        Number(expense.value) * Number(expense.exchangeRates[expense.currency].ask)
      );
    });
    dispatch(SumTotalExpenses(SumTotal));
  };

  render() {
    const { email, totalExpenses } = this.props;
    const currency = 'BRL';
    return (
      <div>
        <div>
          <p data-testid="email-field">{`Email: ${email}`}</p>
          <div>
            <p data-testid="total-field">{totalExpenses}</p>
            <p data-testid="header-currency-field">{currency}</p>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (stateGlobal) => ({
  email: stateGlobal.user.email,
  expenses: stateGlobal.wallet.expenses,
});

Header.propTypes = {
  email: PropTypes.string.isRequired,
  totalExpenses: PropTypes.string.isRequired,
  dispatch: PropTypes.func.isRequired,
  expenses: PropTypes.arrayOf(PropTypes.shape()).isRequired,
};

export default connect(mapStateToProps)(Header);
