import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import emailAction from '../redux/actions';

class Login extends React.Component {
  state = {
    email: '',
    password: '',
    disabledBtn: true,
  };

  onInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    }, this.validateButton);
  };

  validateButton = () => {
    const { email, password } = this.state;
    const validateEmail = /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/;
    const validatePassword = /^.{6,}$/;
    if (validateEmail.test(email) && validatePassword.test(password)) {
      this.setState({ disabledBtn: false });
    } else {
      this.setState({
        disabledBtn: true,
      });
    }
  };

  onClickChange = () => {
    const { history, dispatch } = this.props;
    dispatch(emailAction(this.state));
    history.push('/carteira');
  };

  render() {
    const { email, password, disabledBtn } = this.state;
    return (
      <div>
        <h1>Login</h1>
        <form>
          <input
            name="email"
            data-testid="email-input"
            type="text"
            placeholder="email"
            onChange={ this.onInputChange }
            value={ email }
          />
          <br />
          <input
            data-testid="password-input"
            type="password"
            placeholder="senha"
            name="password"
            onChange={ this.onInputChange }
            value={ password }
          />
          <br />
          <button
            type="submit"
            disabled={ disabledBtn }
            onClick={ this.onClickChange }
          >
            Entrar

          </button>
        </form>
      </div>
    );
  }
}

Login.propTypes = {
  dispatch: PropTypes.func.isRequired,
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default connect()(Login);
