import { CURRENCIES_API, ERROR, REQUEST, SUCCESS, SAVE_EXPENSE, GET_CURRENCIES } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
  totalValue: 0,
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SAVE_EXPENSE:
    return {
      ...state,
      expenses: [...state.expenses, action.expense],
    };
  case GET_CURRENCIES:
    return {
      ...state,
      currencies: action.currencies,
    };
  case REQUEST:
    return {
      ...state,
    };
  case SUCCESS:
    return {
      ...state,
      currencies: Object.keys(action.info),
    };
  case ERROR:
    return {
      ...state,
      currencies: action.error,
    };
  default:
    return state;
  }
};

export default wallet;
