import apiReq from '../../services/Req_api';

export const LOADING_API = 'LOADING_API';
export const REQ_API = 'REQ_API';
export const SALVA_INFOS = 'SALVA_INFOS';
export const SUM_TOT = 'SUM_TOT';

export const loadingApi = () => ({
  type: LOADING_API,
});

export const reqApi = (api) => ({
  type: 'REQ_API',
  currencies: Object.keys(api).filter((currencies) => currencies !== 'USDT'),
});

export const sumTot = (soma) => ({
  type: 'SUM_TOT',
  tot: soma.toFixed(2),
});

export function apiRequi() {
  return async (dispatch) => {
    dispatch(loadingApi());
    const response = await apiReq();
    dispatch(reqApi(response));
  };
}

export const salvaInfos = (informs) => ({
  type: 'SALVA_INFOS',
  informs,
});
