import { ERROR, REQUEST, SUCCESS, EXPENSES,
  EXPENSES_DELETE, EXPENSES_EDIT, SAVE } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case EXPENSES:
    return {
      ...state,
      expenses: [...state.expenses, action.expenses],
    };
  case EXPENSES_DELETE:
    return {
      ...state,
      expenses: state.expenses.filter((e) => e.id !== +action.id),
    };
  case EXPENSES_EDIT:
    return {
      ...state,
      editor: true,
      idToEdit: action.idToEdit,
    };
  case REQUEST:
    return {
      ...state,
    };
  case SUCCESS:
    return {
      ...state,
      currencies: Object.keys(action.info),
    };
  case ERROR:
    return {
      ...state,
      currencies: action.error,
    };
  case SAVE:
    return {
      ...state,
      editor: false,
      expenses: [
        ...state.expenses.map((el) => (el.id === action.expense.id
          ? { ...el, ...action.expense } : el)),
      ],
    };
  default:
    return state;
  }
};

export default wallet;
