const INITIAL_STATE = {
  currencies: [],
};

const wallet = (state = initialState, action) => {
  switch (action.type) {
  case ADD_COINS:
    return {
      ...state,
      currencies: action.coins,

    };
  default:
    return state;
  }
};
export default wallet;
