import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { APICurrencies } from '../redux/actions';

class WalletForm extends Component {
  state = {
    valueinput: 0,
    descriptioninput: '',
    currencyinput: 'USD',
    methodinput: 'Dinheiro',
    taginput: 'Alimentação',
  };

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(APICurrencies());
  }

  handleChange = ({ target }) => {
    this.setState({
      [target.name]: target.value,
    });
  };

  render() {
    const { walletState } = this.props;
    const { currencies } = walletState;
    const { valueinput, descriptioninput,
      currencyinput, methodinput, taginput } = this.state;
    return (
      <div>
        <label htmlFor="value-input">
          Valor:
          <input
            data-testid="value-input"
            type="number"
            name="valueinput"
            value={ valueinput }
            onChange={ this.handleChange }
          />
        </label>

        <label htmlFor="description-input">
          Descrição:
          <input
            data-testid="description-input"
            type="text"
            name="descriptioninput"
            value={ descriptioninput }
            onChange={ this.handleChange }
          />
        </label>

        <label htmlFor="select-currencie">
          Moeda:
          <select
            data-testid="currency-input"
            name="currencyinput"
            value={ currencyinput }
            onChange={ this.handleChange }
          >
            {currencies.map((elemento, index) => (
              <option value={ elemento } key={ index }>
                { elemento }
              </option>
            ))}
          </select>
        </label>

        <label htmlFor="method-input">
          Modo de pagamento:
          <select
            data-testid="method-input"
            name="methodinput"
            value={ methodinput }
            onChange={ this.handleChange }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Cartão de crédito">Cartão de crédito</option>
            <option value="Cartão de débito">Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="tag-input">
          Categoria:
          <select
            data-testid="tag-input"
            name="taginput"
            value={ taginput }
            onChange={ this.handleChange }
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Lazer">Lazer</option>
            <option value="Saúde">Saúde</option>
            <option value="Transporte">Transporte</option>
          </select>
        </label>

      </div>
    );
  }
}

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  walletState: PropTypes.shape().isRequired,
};

const mapStateToProps = (state) => ({
  walletState: state.wallet,
});

export default connect(mapStateToProps)(WalletForm);
