import { ERROR, REQUEST, SUCCESS, ADD_ECONOMIA, ADD_TOTALSUM } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
  totalSum: 0,
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case ADD_ECONOMIA:
    return {
      ...state,
      expenses: [
        ...state.expenses, {
          id: state.expenses.length,
          value: action.expenses.value,
          description: action.expenses.description,
          currency: action.expenses.currency,
          method: action.expenses.method,
          tag: action.expenses.tag,
          exchangeRates: action.exchangeRates,
        }],

    };
  case ADD_TOTALSUM:
    return {
      ...state,
      totalSum: (+action.totalSum
          * +state.expenses[state.expenses.length - 1].exchangeRates[action.coin].ask)
         + state.totalSum,
    };
  default:
    return state;
  }
};

export default wallet;
