import { ADDING_EXPENSES, CURRENCY_ERROR,
  CURRENCY_SUCCESS, EDIT_EXPENSES, REMOVING_EXPENSES, VALID_CURRENCY } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case VALID_CURRENCY:
    return {
      ...state,
    };
  case CURRENCY_SUCCESS:
    return {
      ...state,
      currencies: action.payload,
    };
  case CURRENCY_ERROR:
    return {
      ...state,
      error: action.error,
    };
  case ADDING_EXPENSES:
    return {
      ...state,
      expenses: [...state.expenses, action.payload],
    };
  case REMOVING_EXPENSES:
    return {
      ...state,
      expenses: action.payload,
    };
  case EDIT_EXPENSES:
    return {
      ...state,
      editing: action.payload,
    };
  default:
    return state;
  }
};

export default wallet;
