import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends React.Component {
  render() {
    const { email, expenses } = this.props;
    const totalExpense = expenses.reduce((acc, { value, currency, exchangeRates }) => {
      const currentTotal = acc + Number(value) * Number(exchangeRates[currency].ask);
      return currentTotal;
    }, 0);

    return (
      <div>
        <p id="login" data-testid="email-field">
          <span>{ email }</span>
        </p>
        <center>
          <h1>Header</h1>
          <p data-testid="total-field">
            {totalExpense.toFixed(2)}
          </p>
          <p data-testid="header-currency-field">BRL</p>
        </center>
      </div>
    );
  }
}

Header.propTypes = {
  email: PropTypes.string.isRequired,
  expenses: PropTypes.arrayOf(PropTypes.shape({
    value: PropTypes.string,
    description: PropTypes.string.isRequired,
  })),
};

const mapStateToProps = (estadoGlobal) => ({
  email: estadoGlobal.user.email,
  expenses: estadoGlobal.wallet.expenses,
});

Header.defaultProps = {
  expenses: [],
};

export default connect(mapStateToProps)(Header);
