import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

function Header() {
  const [despesas, setDespesas] = useState([]);

  const expensesArray = useSelector((state) => state.wallet.expenses);

  useEffect(() => {
    expensesArray.forEach(({ currency, value, exchangeRates }) => {
      const currencya = currency;
      const currentValue = parseFloat(value) || 0;
      let convertedtValue = 0;

      Object.values(exchangeRates).forEach((obje) => {
        if (obje.code === currencya) {
          convertedtValue = currentValue * parseFloat(obje.ask);
          setDespesas([...despesas, convertedtValue.toFixed(2)]);
        }
      });
    });
  }, [despesas, expensesArray]);

  const email = useSelector((state) => state.user.email);
  return (
    <>
      <p data-testid="email-field">{email}</p>
      <p data-testid="total-field">0</p>
      <p data-testid="total-field">
        {(despesas.reduce((partialSum, a) => partialSum + parseFloat(a), 0)).toFixed(2)}
      </p>
      <p data-testid="header-currency-field">BRL</p>
    </>
  );
}
export default Header;
