import { ERROR, REQUEST, SUCCESS, INPUT_WALLET_FORM, SUM_TOTAL } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
  total: 0,
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case INPUT_WALLET_FORM:
    return {
      ...state,
      expenses: [...state.expenses, action.payload],
    };
  case SUM_TOTAL:
    return {
      ...state,
      total: action.payload + state.total,
    };
  case REQUEST:
    return {
      ...state,
    };
  case SUCCESS:
    return {
      ...state,
      currencies: Object.keys(action.info),
    };
  case ERROR:
    return {
      ...state,
      currencies: action.error,
    };
  default:
    return state;
  }
};

export default wallet;
