import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getCurrencies, getCurrencieValue } from '../tests/helpers/api';
import { currencieAction, expenseAction } from '../redux/actions';

class WalletForm extends Component {
  constructor() {
    super();

    this.state = {
      id: 0,
      value: '0',
      description: '',
      currency: 'USD',
      paymentMethod: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: {},
    };
  }

  async componentDidMount() {
    const { dispatch } = this.props;
    const currencieArr = await getCurrencies();
    dispatch(currencieAction(currencieArr));
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleClick = async () => {
    const { stateExpense } = props;
    const request = await fetch('https://economia.awesomeapi.com.br/json/all');
    const data = await request.json();
    const objExpense = {
      id: stateExpense.length === 0 ? 0 : stateExpense.length,
      value: amount,
      description,
      currency,
      method,
      tag,
      exchangeRates: data,
    };
    dispatch(saveExpenses(objExpense));
    console.log(objExpense);
    setAmount('');
    setDescription('');
    setCurrency('USD');
    setMethod('Dinheiro');
    setTag('Alimentação');
  };

  render() {
    const { currencies } = this.props;
    const { value, description, currency, paymentMethod, tag } = this.state;
    return (
      <fieldset>
        <label
          htmlFor="valueInput"
        >
          Valor da despesa
          <input
            type="number"
            data-testid="value-input"
            id="valueInput"
            name="value"
            value={ value }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="descInput"
        >
          Descrição da despesa
          <input
            type="text"
            data-testid="description-input"
            id="descInput"
            name="description"
            value={ description }
            onChange={ this.handleChange }
          />
        </label>
        <label
          htmlFor="currency"
        >
          Moeda
          <select
            onChange={ this.handleChange }
            data-testid="currency-input"
            name="currency"
            id="currency"
            value={ currency }
          >
            {currencies
              .map((element) => <option key={ element }>{element}</option>)}
          </select>
        </label>
        <label
          htmlFor="payment"
        >
          Método de pagamento
          <select
            data-testid="method-input"
            id="payment"
            name="payment"
            value={ paymentMethod }
            onChange={ this.handleChange }
          >
            <option value="dinheiro">Dinheiro</option>
            <option value="credito">Cartão de crédito</option>
            <option value="debito">Cartão de débito</option>
          </select>
        </label>
        <label
          htmlFor="tag"
        >
          Tag
          <select
            data-testid="tag-input"
            id="tag"
            name="tag"
            value={ tag }
            onChange={ this.handleChange }
          >
            <option value="alimentacao">Alimentação</option>
            <option value="lazer">Lazer</option>
            <option value="trabalho">Trabalho</option>
            <option value="transporte">Transporte</option>
            <option value="saude">Saúde</option>
          </select>
          <button
            type="button"
            onClick={ this.handleClick }
          >
            Adicionar despesa
          </button>
        </label>
      </fieldset>
    );
  }
}

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  stateExpense: PropTypes.shape({
    length: PropTypes.number,
  }).isRequired,
};

const mapStateToProps = (state) => ({
  stateExpense: state.wallet.expenses,
});

export default connect(mapStateToProps)(WalletForm);
