import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      isSaveButtonDisabled: false,
    };
  }
  onInputChange = (event) => {
    const { name, type, checked } = event.target;
    const value = type === 'checkbox' ? checked : event.target.value;
    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          state = { this.state }
          cardName = { cardName }
          cardDescription = { cardDescription }
          cardAttr1 = { cardAttr1}
          cardAttr2 = { cardAttr2 }
          cardAttr3 = { cardAttr3 }
          cardImage = { cardImage }
          cardRare = { cardRare }
          cardTrunfo = { cardTrunfo }
          isSaveButtonDisabled = { isSaveButtonDisabled }
          onInputChange = { this.onInputChange }
          onSaveButtonClick={ this.onSaveButtonClick }
        />
        <Card 
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
        />
      </div>
    );
  }
}

export default App;
